import React, {createContext, useState} from 'react';
import {updateDisplayName, updateDisplayIcon, updateActivityStatus, 
    appendFriend, deleteFriend, blackListUser, whiteListUser, alterActiveDecks} from '../../firebase/firebase.utils';

export const UserContext = createContext({
    currentUser: {
        id: null,
        displayName: "",
        displayIcon: null,
        friendsList: [],
        blackList: [],
        activityStatus: "offline",
        activeDecks: [], //array of objects -> {name, language, [items]}; items-> {frontSide="", backSide=""}
    },
    changeCurrentUser: () => {},
    changeDisplayName: () => {},
    changeDisplayIcon: () => {},
    changeActivityStatus: () => {},
    changeActiveDecks: () => {},
    addFriend: () => {},
    deleteFriend: () => {},
    blockUser: () => {},
    unblockUser: () => {}
});

const UserProvider = ({children}) => {

    const [displayName, setDisplayName] = useState("");
    const [displayIcon, setDisplayIcon] = useState(null);
    const [friendsList, setFriendsList] = useState([]);
    const [blackList, setBlackList] = useState([]);
    const [activeDecks, setActiveDecks] = useState([]);
    const [activityStatus, setActivityStatus] = useState("offline");
    const [currentUser, setCurrentUser] = useState(null);
    
    const changeActiveDecks = activeDecks => {
        setActiveDecks(alterActiveDecks(this.currentUser.id, activeDecks))
    }

    const changeDisplayName = name => {
        setDisplayName(updateDisplayName(this.currentUser.id, name));
        //may be redundant to set the state if the state is just going to be refreshed by onSnapshot
    }

    const changeDisplayIcon = image => {
        setDisplayIcon(updateDisplayIcon(this.currentUser.id, image));
    }

    const addFriend = friend => {
        unblockUser(this.currentUser.id, this.currentUser.blackList, friend);
        setFriendsList(appendFriend(this.currentUser.id, this.currentUser.friendsList, friend));
    }

    const removeFriend = friend => {
        setFriendsList(deleteFriend(this.currentUser.id, this.currentUser.friendsList, friend));
    }

    const blockUser = user => {
        //if the user exists in the friendsList remove them then black list them
        deleteFriend(this.currentUser.id, this.currentUser.friendsList, user);
        setBlackList(blackListUser(this.currentUser.id, this.currentUser.blackList, user));
    }

    const unblockUser = user => {
        setBlackList(whiteListUser(this.currentUser.id, this.currentUser.blackList, user));
    }

    const changeActivityStatus = () => {
        setActivityStatus(updateActivityStatus(this.currentUser.id, this.currentUser.activityStatus));
    }

    const changeCurrentUser = user => {
        setCurrentUser(user);
    }

    return <UserContext.Provider
    value={{currentUser, displayName, displayIcon, activityStatus, activeDecks, friendsList, blackList, changeActiveDecks, changeCurrentUser, changeActivityStatus, changeDisplayName, changeDisplayIcon, addFriend, removeFriend, blockUser, unblockUser}}>{children}</UserContext.Provider>
}

export default UserProvider;
