import React, {useState} from 'react';
import {CardContainer} from './card.styles';

const Card = ({card}) => {
    const [front, flip] = useState(true)

    console.log(card);
    //probably going to need a little more styling here? Potentially if I want the card to look extra pretty
    return <CardContainer onClick={flip(!front)} frontSide={front}><p>{front ? card.frontText : card.backText}</p></CardContainer>
}


export default Card;
