import styled from 'styled-components';
import {Link} from 'react-router-dom'



export const SignInPageContainer = styled.div`
  width: 850px;
  display: flex;
  flex-direction: column;
  margin: 30px 425px;
  @media screen and (max-width: 800px) {
    margin-top: 30px;
    margin-bottom: 30px;
    margin-right: 0px;
    margin-left: 10px;
