import React, {useState, useContext, useEffect} from 'react';
import {withRouter} from 'react-router-dom';
import ProfileTab from '../../components/profile-tab/profile-tab.component';
import CustomButton from '../custom-button/custom-button.component';
import {UserContext} from '../../providers/user/user.provider';
import {fetchUserArray, auth} from '../../firebase/firebase.utils';
import {BrowseFriendsContainer, TabBanner, DisplayContainer} from './browse-friends.styles';

const BrowseFriends = ({history}) => {

  const {id, friendsList} = useContext(UserContext);
  console.log("ID: ", id, "authID", auth.email, "FriendsList: ", friendsList);
  const [userArray, setUserArray] = useState([]);


    useEffect(() => {
      async function fetchData() {
        const fetchedUserArray = await fetchUserArray();
        if (!fetchedUserArray) return console.log("Error");
        setUserArray(fetchedUserArray);
      }
      fetchData();
    }, [])


    const handleClick = async event => {
       await history.push(`/profile/:userId/${userArray[event.id].id}`);
    }
   
    (async() => console.log("userArray: ", await userArray))()
    //TODO design TabHeader --> TabBanner will be deleted once TabHeader is designed
    return <BrowseFriendsContainer>
        <TabBanner>Browse Users</TabBanner>
        {userArray.filter(async (item) => await item.id !== id && !friendsList.find((friend) => item.id === friend.currentUser.id)).map((user, idx) => <DisplayContainer key={idx}><ProfileTab user={user}/><CustomButton id={idx} onClick={handleClick} isProfileStyles>View Profile</CustomButton></DisplayContainer>)}
    </BrowseFriendsContainer>
}

export default withRouter(BrowseFriends);
