import styled, {css} from 'styled-components';

const getBlurEffectStyle = ({popUp}) => {
    
    if (popUp)
        return blurEffectStyle;
    else
        return null;
}

export const PersonalProfileContainer = styled.div`
    display: flex;
    flex-direction: column;
    margin: 50px;
    ${'' /* personalProfileContainer */}
`;

export const HeaderButtonsContainer = styled.div`
    text-align: right;
`;

export const ImageContainer = styled.div`

`;

export const ProfileIconContainer = styled.div`
    ${getBlurEffectStyle}
    border-radius: 50%;

`;

export const ButtonsContainer = styled.div`
    ${getBlurEffectStyle}

`;

export const DisplayNameContainer = styled.span`
    ${getBlurEffectStyle}
`;

export const FormContainer = styled.div`
    ${getBlurEffectStyle}
`;

export const FileInput = styled.input`
  opacity: 0;
  `;

export const CancelButtonWithTooltip = styled.div`
  position: relative;
  display: inline-block;
  cursor: pointer;
`;

export const TooltipText = styled.span`
  
  visibility: hidden;
  width: 120px;
  background-color: black;
  color: #fff;
  text-align: center;
  border-radius: 6px;
  padding: 5px 0;

  position: absolute;
  z-index: 1;

  &:hover {
    visibility: visible
  }
`;

const blurEffectStyle = css`
    filter: blur(8px);
`;
