import ThirtienthImage from './2020-05-08 16_31_42-React App.png'

export const WEBSITE_TEXT = {
    welcome_text: "Welcome to LangFlash the flashcard language learning app (version 1.0.0)",
    instruction_one: "Create an account or make a guest account.",
    instruction_two: "Navigate to the user profile.",
    instruction_three: "Click on the deck button.",
    instruction_four: "In the deck menu you can edit or delete existing decks or add new ones.",
    instruction_five: "Click on the create a deck button to create a new deck.",
    instruction_six: "Click on the deck that you want to edit; clicking on an individual deck will open the card menu",
    instruction_seven: "In the Card menu you can view, add, and delete exisiting cards as well add new ones. Click on an existing card to flip it over.",
    instruction_eight: "This application also allows for managing friends and user-browsing. A user may view another users profile and browse through their deck.",
    instruction_nine: "\nIn the next future this application will allow users to chat with other users, will enforce profile privacy settings, and allow users to copy decks or cards from other users and monitor friends' activity.",
