    opened_deck_window_img: SecondImage,
    add_card_img: ThirdImage,
    delete_card_img: FourthImage,
    navigate_to_profile_img: FifthImage,
    create_account: SixthImage,
    delete_deck_img: SeventhImage,
    add_deck_img: EighthImage,
    edit_deck_image: NinthImage,
    view_card_img: TenthImage,
