import React, {useContext} from 'react';
import {UserContext} from '../../providers/user/user.provider';
import {ProfileDisplayContext} from '../../providers/profile-display/profile-display.provider';
import {ProfileIconContainer} from './profile-icon.styles';

const ProfileIcon = () => {
    const {toggleHidden} = useContext(ProfileDisplayContext);
    const {currentUser} = useContext(UserContext);

    //console.log( "currentUser ", currentUser, currentUser.currentUser, "displayIcon: ", displayIcon)
    
    return <ProfileIconContainer onclick={toggleHidden}>
    <img style={{borderRadius: '100px'}} alt='default-profile'  
    src={currentUser.displayIcon ? currentUser.displayIcon : currentUser.currentUser.displayIcon}/></ProfileIconContainer>
}

export default ProfileIcon;
