import React, {useContext} from 'react';
import {UserContext} from '../../providers/user/user.provider';
import {ProfileDisplayContext} from '../../providers/profile-display/profile-display.provider';
import {ProfileIconContainer} from './profile-icon.styles';

const ProfileIcon = () => {
    const {toggleHidden} = useContext(ProfileDisplayContext);
    const {currentUser, currentUser: {displayIcon}} = useContext(UserContext);

    console.log( "currentUser ", currentUser, "displayIcon: ", displayIcon)
    return <ProfileIconContainer onclick={toggleHidden}>{currentUser.displayIcon}</ProfileIconContainer>
}

export default ProfileIcon;
