import styled, {css} from 'styled-components';


export const DecksButtonContainer =  styled.button`
    cursor: pointer;  
`;

const getButtonStyles = ({addStyles, ...props}) => {
    console.log(props, props.addStyles, addStyles);

    if (addStyles) {
        return AddDeckStyles;
    }
    return RegularDeckStyles;
}

export const LeftPane =  styled.div`
    z-index: 0;
    background-color:white;
    height: 55px;
    width: 55px;
    left: 30px;
    border: 2px #a9a9a9 solid;
    border-radius: 25%;
    ${getButtonStyles};
`;

export const MiddlePane = styled.div`
    z-index: 1;
    background-color:white;
    height: 55px;
    width: 55px;
    left: 50px;
    border: 2px #a9a9a9 solid;
    border-radius: 25%;
    ${getButtonStyles}
`;

export const RightPane = styled.div`
    z-index: 2;
    background-color:white;
    height: 55px;
    width: 55px;
    left: 70px;
    border: 2px #a9a9a9 solid;
    border-radius: 25%;
    ${getButtonStyles}
`;

export const PlusIcon = styled.span`
    postion: relative;
    z-index: 2;
    left: 70px;
    height: 75px;
`
const AddDeckStyles = css`
    position: relative;
    background-color: blue;
`;

const RegularDeckStyles = css`
    position: absolute;
`;
