import React, {useContext}from 'react';
import SignUp from '../../components/sign-up/sign-up.component';
import {SignUpPageContainer} from './sign-up-page.styles';
import {UserContext} from '../../providers/user/user.provider'
import {updateActivityStatus} from '../../firebase/firebase.utils';


const SignUpPage = () => {
    const {id, activityStatus} = useContext(UserContext);
    return <SignUpPageContainer>
        <UserContext.Provider changeActivityStatus={()=>updateActivityStatus(id, activityStatus)}>
            <SignUp/>
        </UserContext.Provider>
    </SignUpPageContainer>
}

export default SignUpPage;
