import styled from 'styled-components';

export const ProfileDropdownContainer = styled.div`
  position: absolute;
  width: 240px;
  height: 340px;
  display: flex;
  flex-direction: column;
  padding: 20px;
  border: 1px solid black;
  background-color: white;
  top: 90px;
  right: 40px;
  z-index: 5;
`;

export const LabelContainer = styled.span`
    font-size: 18 px;
    text-align: center;
`;
