import styled, {css} from 'styled-components';

const getButtonStyles = ({isAdd}) => {
    console.log(isAdd);

    if (isAdd) {
        return AddDeckStyles;
    }
    return RegularDeckStyles;
}

export const DecksButtonContainer =  styled.div`
    cursor: pointer;  
`;

export const LeftPane =  styled.div`
    z-index: 0;
    background-color:white;
    height: 55px;
    width: 55px;
    left: 30px;
    border: 2px #a9a9a9 solid;
    border-radius: 25%;
    ${getButtonStyles};
`;

export const MiddlePane = styled.div`
    z-index: 1;
    background-color:white;
    height: 55px;
    width: 55px;
    left: 50px;
    border: 2px #a9a9a9 solid;
    border-radius: 25%
`;

export const RightPane = styled.div`
    z-index: 2;
    background-color:white;
    height: 55px;
    width: 55px;
    left: 70px;
    border: 2px #a9a9a9 solid;
    border-radius: 25%
`;

export const PlusIcon = styled.span`
    postion: absolute;
    z-index: 2;
    left: 70px;
    height: 75px;
`
const AddDeckStyles = css`
    position: relative;
`;

const RegularDeckStyles = css`
    position: absolute;
`;
