import React, {useContext} from 'react';
import {SignInPageContainer, LinksContainer} from './sign-in-page.styles';
import SignInComponent from '../../components/sign-in/sign-in.component';
import {SignUpLink} from '../../reusable-styles/sign-up-link';
import {UserContext} from '../../providers/user/user.provider';
import {updateActivityStatus} from '../../firebase/firebase.utils';


const SignInPage = () => {
    const {id, activityStatus} = useContext(UserContext);
    return <SignInPageContainer>
        <UserContext.Provider changeActivityStatus={()=>updateActivityStatus(id, activityStatus)}>
            <SignInComponent/>
        </UserContext.Provider>
        <LinksContainer>
            <SignUpLink to="/signup">
                Don't have an account? Sign up for free.
            </SignUpLink>
        </LinksContainer>
    </SignInPageContainer>

}

export default SignInPage;
