import React, {useContext} from 'react';
import {withRouter} from 'react-router-dom';
import ProfileTab from '../../components/profile-tab/profile-tab.component';
import CustomButton from '../custom-button/custom-button.component';
import {UserContext} from '../../providers/user/user.provider';
import {fetchUserArray} from '../../firebase/firebase.utils';
import {BrowseFriendsContainer, TabBanner, DisplayContainer} from './browse-friends.styles';

const BrowseFriends = ({history}) => {

    

    const {currentUser: {id, friendsList}} = useContext(UserContext);
    
    const userArray = [];
    fetchUserArray().then((array) => {
        array.map((value, idx) => userArray.push(Object.keys(value)[idx]))
        userArray.filter((item) => item.id !== id && !friendsList.find((friend) => item.id === friend.currentUser.id));
    })
    
    console.log(userArray);
    const handleClick = event => {
        history.push(`/profile/:userId/${userArray[event.id].id}`);
    }
   
    //TODO design TabHeader --> TabBanner will be deleted once TabHeader is designed
    return <BrowseFriendsContainer>
        <TabBanner>Browse Users</TabBanner>
        {userArray.map((user, idx) => <DisplayContainer><ProfileTab user={user}/><CustomButton id={idx} onClick={handleClick} isProfileStyles>View Profile</CustomButton></DisplayContainer>)}
    </BrowseFriendsContainer>
}

export default withRouter(BrowseFriends);
