import styled from 'styled-components';

export const CardButtonContainer = styled.div`
    height: 125px;
    width: 125px;
    text-align: center;
    background-color: white;
    border: solid #a9a9a9;
    border-radius: 15%;
    margin-top: 60px;
    cursor: pointer;
`;
export const PlusIcon = styled.div`
    width: 75px;
    position: relative;
    z-index: 2;
    left: 20px;
    height: 60px
`
